import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./proposalCompnents.scss";
import Label from "../../../../../components/customLabel/label";
import DropdownList from "../../../../../components/dropdowns/dropdownList";
import { ENDPOINT } from "../../../../../utils/endpoint";
import { getOptions, postOptions } from "../../../../../utils/httpConfig";
import request from "../../../../../utils/request";
import { store } from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import Radio from "../../../../../components/radioButton/radio";
import notifications from "../../../../../utils/notifications";
import {
  GetAmountPerDay,
  GetAmountPerHour,
} from "../../../../../utils/currency";
import FreelancerSelection from "./freelancerSelection";
let teamMembers = [
  {
    name: "Asim ALi",
    profile:
      "https://dhihitu47nqhv.cloudfront.net/profileimage/64205290-20ef-4083-8d04-02033194bf73.png",
    jobType: "ReactJs developer",
    available: "Part Time",
    hourlyRate: "0.00",
    availablePerWeek: "30",
    dailyRate: "120",
  },
  {
    name: "Mansure Haider",
    profile:
      "https://lh3.googleusercontent.com/a-/AOh14GjK1QIGlo0M7b0EvnbpUV8gpL730CC4kH_LG0JG-DA=s96-c",
    jobType: "C# developer",
    available: "Full Time",
    hourlyRate: "0.00",
    availablePerWeek: "30",
    dailyRate: "150",
  },
  {
    name: "Sonny Cho",
    profile:
      "https://dhihitu47nqhv.cloudfront.net/profileimage/64205290-20ef-4083-8d04-02033194bf73.png",
    jobType: "Dot net, developer",
    available: "Part Time",
    hourlyRate: "0.00",
    availablePerWeek: "30",
    dailyRate: "100",
  },
  {
    name: "Canh doan",
    profile:
      "https://dhihitu47nqhv.cloudfront.net/profileimage/64205290-20ef-4083-8d04-02033194bf73.png",
    jobType: "C# developer",
    available: "Part Time",
    hourlyRate: "0.00",
    availablePerWeek: "30",
    dailyRate: "120",
  },
  {
    name: "Fiaz Ahmed",
    profile:
      "https://dhihitu47nqhv.cloudfront.net/profileimage/64205290-20ef-4083-8d04-02033194bf73.png",
    jobType: "C# developer",
    available: "Part Time",
    hourlyRate: "0.00",
    availablePerWeek: "30",
    dailyRate: "120",
  },
];
let teams = [
  {
    text: "team 1",
    value: "team 1",
  },
  {
    text: "team 2",
    value: "team 2",
  },
  {
    text: "team 3",
    value: "team 3",
  },
  {
    text: "team 4",
    value: "team 4",
  },
  {
    text: "team 5",
    value: "team 5",
  },
];
export default function FreeContract(props) {
  const [currency, setCurrency] = useState(
    props.selectedProject.currencyCode ? props.selectedProject.currencyCode : ""
  );
  const [proposalHours, setProposalHours] = useState("");
  const [proposingAmountHourly, setProposingAmountHourly] = useState("");
  const [proposingAmountDaily, setProposingAmountDaily] = useState("");
  const [weekDays, seSpecificWeekDays] = useState([]);
  const [proposalDays, setProposalDays] = useState("");
  const [anyDaysFlag, setaSnyDaysFlag] = useState(false);
  const [termsFlag, setTermsFlag] = useState(false);
  const [freelancerType, setFreelancerType] = useState("");
  const [loading, setLoading] = useState(false);
  const [flag, setFlag] = useState(false);
  const [isProbationAccepted, setIsProbationAccepted] = useState(false);
  const [daysAlert, setDaysAlert] = useState(false);
  const [errMsg, setErrMsg] = useState({});
  const [selectedTeams, setSelectedTeams] = useState([
    {
      teamName: "",
      teamMembers: [],
    },
  ]);
  const [SelectedFreelancer, setSelectedFreelancer] = useState([]);
  const dispatch = useDispatch();

  const languageType = useSelector(
    (state) => state.languageReducer.languageType
  );

  const authReducer = useSelector((state) => state.authReducer);

  const languageReducer = useSelector((state) => state.languageReducer);

  React.useEffect(() => {
    if (authReducer.freelancerAuth) {
      setFreelancerType("Freelancer");
    }
    if (authReducer.organizationAuth) {
      setFreelancerType("Company");
    }
  }, [authReducer.freelancerAuth, authReducer.organizationAuth]);

  const handleValidation = () => {
    let errorMessage = {};
    let { proposalData, languageType } = props;
    let formIsValid = true;
    if (!proposalData.coverLetter) {
      formIsValid = false;
      props.handleInitialValidation("coverLetter");
    } else if (!proposalData.description) {
      formIsValid = false;
      props.handleInitialValidation("description");
    } else if (!currency) {
      formIsValid = false;
      errorMessage["currency"] = languageType.REQUIRED_MESSAGE;
    } else if ( freelancerType === "Freelancer" &&  !proposingAmountHourly) {
      formIsValid = false;
      errorMessage["proposingAmountHourly"] = languageType.REQUIRED_MESSAGE;
    } else if (freelancerType === "Freelancer" &&  !proposingAmountHourly) {
      formIsValid = false;
      errorMessage["proposingAmountHourly"] = languageType.REQUIRED_MESSAGE;
    } else if (freelancerType === "Freelancer" &&  !proposingAmountDaily) {
      formIsValid = false;
      errorMessage["proposingAmountDaily"] = languageType.REQUIRED_MESSAGE;
    } else if (!proposalHours) {
      formIsValid = false;
      errorMessage["proposalHours"] = languageType.REQUIRED_MESSAGE;
    } else if ( freelancerType === "Freelancer" &&  !proposalDays) {
      formIsValid = false;
      errorMessage["proposalDays"] = languageType.REQUIRED_MESSAGE;
    } else if (!anyDaysFlag && weekDays.length === 0) {
      formIsValid = false;
      errorMessage["weekDays"] = languageType.REQUIRED_MESSAGE;
    } else if (
      freelancerType === "Company" &&
      props.companyFreelancers.length < 1
    ) {
      formIsValid = false;
      notifications.showWarning("You need at least freelance for proposal");
    } else if (freelancerType === "Company" && SelectedFreelancer.length < 1) {
      formIsValid = false;
      notifications.showWarning("You need to select at least one freelancer!");
    }else if (!termsFlag) {
      formIsValid = false;
      errorMessage["termsFlag"] = languageType.REQUIRED_MESSAGE;
    }
    /* proposalDays */
    setErrMsg(errorMessage);
    return formIsValid;
  };
  const handleUpdateWeekDays = (value, day) => {
    if (Number(proposalDays) && Number(proposalDays) > 0) {
      if (weekDays.length < Number(proposalDays)) {
        if (value) {
          let specificWeekDay = weekDays;
          specificWeekDay.push(day);
          seSpecificWeekDays(specificWeekDay);
          setFlag(flag ? false : true);
        } else {
          let specificWeekDay = weekDays;
          specificWeekDay.splice(specificWeekDay.indexOf(day));
          seSpecificWeekDays(specificWeekDay);
          setFlag(flag ? false : true);
        }
        setDaysAlert(false);
      } else {
        setDaysAlert(true);
      }
    } else {
      setDaysAlert(null);
    }
    let errMsgs = errMsg;
    delete errMsgs.weekDays;
    setErrMsg(errMsgs);
  };
  const handleUpdateTermsFlag = (value) => {
    if (value) {
      let errMsgs = errMsg;
      delete errMsgs.termsFlag;
      setErrMsg(errMsgs);
    } else {
      let errMsgs = errMsg;
      errMsgs["termsFlag"] = props.languageType.REQUIRED_MESSAGE;
      setErrMsg(errMsgs);
    }
    setTermsFlag(value);
  };
  const handleUpdateIsProbation = (value) => {
    setIsProbationAccepted(value);
  };

  React.useEffect(() => {
    let {
      hoursPerWeek,
      hourlyAmount,
      amountPerDay,
      weekDays,
      daysAttendingPerWeek,
      isProbationAccepted,
    } = props.existingProposalData;
    setProposalHours(hoursPerWeek);
    setProposingAmountHourly(hourlyAmount);
    setProposingAmountDaily(amountPerDay);

    if (weekDays && JSON.parse(weekDays).length > 0) {
      seSpecificWeekDays(weekDays);
      setaSnyDaysFlag(true);
    } else {
      setaSnyDaysFlag(false);
    }
    setProposalDays(daysAttendingPerWeek);

    setIsProbationAccepted(isProbationAccepted);
  }, [props.existingProposalData]);

  const handleProposalSubmit = async () => {
    let { proposalData, selectedProject } = props;
    if (handleValidation()) {
      let params = {
        projectId: proposalData.projectId,
        freelancerReferenceId: proposalData.userId,
        projectProposalId: proposalData.projectProposalId,
        freelancerType:
        selectedProject.freelancerType === " "
        ? "Individual"
        : selectedProject.freelancerType || "Individual",
        proposedDateTime: "",
        description: proposalData.description,
        coverLetter: proposalData.coverLetter,
        screeningQuestions:
          proposalData.screeningQuestionList.length > 0
            ? JSON.stringify(proposalData.screeningQuestionList)
            : "",
        isProbationAccepted: isProbationAccepted,
        daysAttendingPerWeek: proposalDays?.toString(),
        amountPerDay: proposingAmountDaily?.toString(),
        weekDays: JSON.stringify(weekDays),
        hoursPerWeek: proposalHours,
        hourlyAmount: proposingAmountHourly?.toString(),
        estimateCompleteDateTime: "",
        amount: "",
        milestones: "",
        documents:props.documents,
        currencyCode: currency,
        offerProjectPeriod: "",
        offeredMilestones: null,

        freelancers: SelectedFreelancer.length>0?SelectedFreelancer.map((item)=>({
          individualFreelancerId: item.individualFreelancerId,
          attendHourOfWeek: proposalHours,
          hourlyRate: item.hourlyRate,
          dailyRate: item.dailyRate,
      })):null
      };
      setLoading(true);
      let result = await request(
        proposalData.projectProposalId
          ? ENDPOINT["UpdateProposal"]
          : ENDPOINT["CreateProposal"],
        postOptions(params)
      );

      if (result.success) {
        if (result) {
          setLoading(false);
          store.addNotification({
            title: `Proposal ${
              proposalData.projectProposalId ? "Updated" : "Submitted"
            } Successfully `,
            message: `Proposal submitted successfully ${
              proposalData.projectProposalId ? "Updated" : "Submitted"
            } again Free Contract project`,
            type: "success",
            insert: "top",
            container: "top-center",
            animationIn: ["animate__animated", "animate__fadeIn"],
            animationOut: ["animate__animated", "animate__fadeOut"],
            dismiss: {
              duration: 2000,
              onScreen: true,
            },
          });
          let newurl = `project-proposal-detail?id=${proposalData.projectId}&projectProposalId=${result.result}`;
            props.onRouteChange("project-proposal-detail");
            props.history.push(newurl);
        } else {
          store.addNotification({
            title: "Error ",
            message: result.message,
            type: "warning",
            insert: "top",
            container: "top-center",
            animationIn: ["animate__animated", "animate__fadeIn"],
            animationOut: ["animate__animated", "animate__fadeOut"],
            dismiss: {
              duration: 2000,
              onScreen: true,
            },
          });
          setLoading(false);
        }
      }
    }
  };

  return (
    <div className="milestone_project_proposal_area">
      <div className="milestone_project_proposal_detail">
        {/* <Label  title={"Propose Description"} compulsory={true} /> */}
        <div className="row">
          <div
            className="col-12 col-md-2"
            hidden={freelancerType === "Company"}
          >
            <DropdownList
              id="currency"
              name="currency"
              enableAutoCompleteSearch
              placeHolder="currency"
              value={currency}
