                    {" "}
                    <img src={"https://jungleworksassetsbucket.s3.ap-northeast-2.amazonaws.com/bearoleImages/hire/accountingIcon.svg"} alt="" />{" "}
                  </span>
                </Link>
              </div>
            </div>

            <div className="tool_icon">
              <div
                onMouseEnter={() => {
                  this.onIconChange("marketingBlackIcon", "marketingColor.svg");
