import React, { useState } from "react";
import Heading from "../../../components/freelancerCreation/heading";
import Radio from "../../../components/radioButton/radio";
import Label from "../../../components/postProject/label";
import "./headHunter.scss";

function ConditionSetup(props) {
  const [resumeView, setResumeView] = useState("");
  const [jobAvailability, setJobAvailability] = useState("");
  const [registration, setRegistration] = useState("");
  return (
    <section className="card_sec" style={{ padding: "0px" }}>
      <Heading heading={"Condition Set Up "} step={"3"} shadow={true} />{" "}
      <div hidden={true} className="text-danger">
        {" "}
        Sorry this service is currently not available until 2021 jan
      </div>
      <br />
      <div className="condition-setup-area-head-hunter">
        <Label
          title={"Set your consultant No. of Resume View "}
          prefixBoxValid={true}
          prefixBoxInValid={false}
          primary={true}
          bold={true}
        ></Label>
        <div className="conditional-checkboxes-radio-area">
          <Radio
            handleSelect={(value) => {
              setResumeView("1000");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={resumeView === "1000" ? true : false}
            label={"Up to 1,000 view"}
            compulsory={false}
            disableCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setResumeView("2000");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={resumeView === "2000" ? true : false}
            label={"Up to 2,000 view"}
            compulsory={false}
            disableCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setResumeView("3000");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={resumeView === "3000" ? true : false}
            label={"Up to 3,000 view"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setResumeView("Unlimited");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={resumeView === "Unlimited" ? true : false}
            label={"Unlimited"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setResumeView("CustomerLevel");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={resumeView === "CustomerLevel" ? true : false}
            label={"CustomerLevel"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
        </div>
      </div>
      <div className="condition-setup-area-head-hunter">
        <Label
          title={"Set your consultant No. Job Offer availability Per Month "}
          prefixBoxValid={true}
          prefixBoxInValid={false}
          primary={true}
          bold={true}
        ></Label>
        <div className="conditional-checkboxes-radio-area">
          <Radio
            handleSelect={(value) => {
              setJobAvailability("50");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={jobAvailability === "50" ? true : false}
            label={"Up to 50 offers"}
            compulsory={false}
            disableCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setJobAvailability("100");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={jobAvailability === "100" ? true : false}
            label={"Up to 100 offers"}
            compulsory={false}
            disableCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setJobAvailability("200");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={jobAvailability === "200" ? true : false}
            label={"Up to 200 offers"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setJobAvailability("Unlimited");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={jobAvailability === "Unlimited" ? true : false}
            label={"Unlimited"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setJobAvailability("CustomerLevel");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={jobAvailability === "CustomerLevel" ? true : false}
            label={"CustomerLevel"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
        </div>
      </div>
      <div className="condition-setup-area-head-hunter">
        <Label
          title={"Set No. of Contract Registration Per Month "}
          prefixBoxValid={true}
          prefixBoxInValid={false}
          primary={true}
          bold={true}
        ></Label>
        <div className="conditional-checkboxes-radio-area">
          <Radio
            handleSelect={(value) => {
              setRegistration("50");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={registration === "50" ? true : false}
            label={"Up to 50 Contract"}
            compulsory={false}
            disableCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setRegistration("100");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={registration === "100" ? true : false}
            label={"Up to 100 Contract"}
            compulsory={false}
            disableCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setRegistration("200");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={registration === "200" ? true : false}
            label={"Up to 200 Contract"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setRegistration("Unlimited");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={registration === "Unlimited" ? true : false}
            label={"Unlimited"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
          <Radio
            handleSelect={(value) => {
              setRegistration("CustomerLevel");
            }}
            name="consultant-no"
            id="consultant-no-option"
            checked={registration === "CustomerLevel" ? true : false}
            label={"CustomerLevel"}
            compulsory={false}
            disabl
            eCustomControl={true}
          />
        </div>
      </div>
      <br />
      <br />
      <br />
      <div className="create-freelancer-bottom-steps">
        <button
          onClick={() => {
            props.handleBack("CreateFreelancerBasicInfo");
          }}
          className="create-freelancer-bottom-steps-back"
        >
          Back
        </button>
        <button
          onClick={() => {
            props.handleNext("Invitation");
            window.scrollTo({
              top: "0",
              behavior: "smooth",
            });
          }}
          className="create-freelancer-bottom-steps-skip"
        >
          Next
        </button>
      </div>
    </section>
  );
}

export default ConditionSetup;
