  companyType: null,
  businessType: "web",
  profileImageUrl: null,
  companyImages: null,
  contactNumber: "3026046119",
  emailAddress: null,
  website: "asimportfolio-6fd1d.web.app",
  noOfEmployee: "1542",
  officeLocation: null,
  isActive: false,
  isAgent: false,
  isAllowToWorkAsIndividualFreelancer: false,
  employeeInvitation: null,
  ndaId: null,
  stockExchange: null,
  registeredUserType: null,
